data.query:
query ListOfAppointments(
  $storeId: String
  $startDatetime: String!
  $endDatetime: String!
  $status: String
) {
  encounters(
    storeId: $storeId
    filter: {
      program: { equalTo: "HIVCareProgram" }
      status: { equalTo: $status }
      startDatetime: {
        afterOrEqualTo: $startDatetime
        beforeOrEqualTo: $endDatetime
      }
    }
  ) {
    ... on EncounterConnector {
      nodes {
        events(
          at: $endDatetime
          filter: { type: { equalAny: ["viralLoadResultDatetime"] } }
        ) {
          type
          data
        }
        startDatetime
        patient {
          name
          id
          dateOfBirth
          gender
        }
        status
      }
      totalCount
    }
  }
}

