data.query:
query ViralLoadEligibility($storeId: String, $now: String) {
  store(id: $storeId) {
    ... on StoreNode {
      storeName
      logo
    }
  }

  patients(storeId: $storeId) {
    ... on PatientConnector {
      nodes {
        firstName
        lastName
        gender
        phone
        programEnrolments(filter: { program: { equalTo: "HIVCareProgram" } }) {
          programEnrolmentId
          encounters(
            page: { first: 1, offset: 0 }
            sort: { key: startDatetime, desc: true }
            filter: {
              program: { equalTo: "HIVCareProgram" }
              status: { equalTo: "COMPLETED" }
              startDatetime: { beforeOrEqualTo: $now }
            }
          ) {
            nodes {
              events(
                at: $now
                filter: {
                  type: {
                    equalAny: [
                      "viralLoadResultDatetime"
                      "viralLoadResult"
                      "viralLoadTargetting"
                    ]
                  }
                }
              ) {
                type
                data
              }
              startDatetime
            }
          }

          nextScheduledEncounters: encounters(
            page: { first: 1, offset: 0 }
            sort: { key: startDatetime, desc: false }
            filter: {
              program: { equalTo: "HIVCareProgram" }
              status: { equalTo: "SCHEDULED" }
              startDatetime: { afterOrEqualTo: $now }
            }
          ) {
            nodes {
              startDatetime
            }
          }
        }
      }
    }
  }
}

style.css.data:
@page {
  margin: 0;
  size: A4 landscape;
}

h1 {
  width: fit-content;
  margin: 7 auto;
  padding: 0px 30px;
  border: 2px solid black;
  box-shadow: 5px 5px 0px black;
  font-size: 15px;
  margin-bottom: 10px;
}

h2 {
  width: fit-content;
  margin: 5 auto;
  font-size: 15px;
}

.store-logo-container {
  position: absolute;
  top: 5;
  right: 30;
}

.store-logo-container .logo {
  display: block;
  width: 70px;
  height: 70px;
}

.container {
  max-width: 1000px;
  margin: auto;
  padding: 10px;
  border-top: 2px solid black;
  font-size: 12px;
  font-family: "Helvetica Neue", "Helvetica", Helvetica, Arial, sans-serif;
  color: #555;
}

.container table {
  width: 100%;
  font-size: inherit;
  font-family: inherit;
  text-align: left;
  margin-top: 10;
  margin-bottom: 15;
}

.container table td {
  padding: 5px;
  vertical-align: top;
}

.container table tr.heading td {
  font-weight: bold;
  margin-bottom: 15px;
}

data.template:
{% set_global timezone = "Pacific/Port_Moresby" %}

<style>
  {% include "style.css" %}
</style>

<body>
  <h1>Viral Load Eligibility</h1>
  <h2>for</h2>
  <h2>{{data.store.storeName}}</h2>
  {% if data.store.logo %}
  <div class="store-logo-container">
  <img class="logo" id='store-logo' src='{{data.store.logo}}' />
  </div>
  {% endif %}

  <div class="container">
  <table>
    <thead>
      <tr class="heading">
        <td>Next Scheduled Appointment</td>
        <td>Patient ID</td>
        <td>Last Viral Load Date</td>
        <td>Last Viral Load</td>
        <td>Type of VL Collection</td>
        <td>First Name</td>
        <td>Last Name</td>
        <td>Sex</td>
        <td>Last Visit Date</td>
        <td>Contact</td>
      </tr>
    </thead>
    <tbody>
    {% for patient in data.patients.nodes %}
      {% if patient.programEnrolments | length > 0 %}
        {% set enrolment = patient.programEnrolments.0 %}
        {% set encounterNotNull = enrolment.encounters.nodes.0 | default(value="{}") %}
        {% set eventsByType = encounterNotNull.events | default(value=[]) | group_by(attribute="type") %}

        <tr>
          <td>
            {% if enrolment.nextScheduledEncounters.nodes.0 %}
              {{enrolment.nextScheduledEncounters.nodes.0.startDatetime | date(format="%d/%m/%Y",timezone=timezone)}}
            {% endif %}
          </td>
          <td>{{enrolment.programEnrolmentId}}</td>
          <td>
            {% if eventsByType.viralLoadResultDatetime.0 %}
              {{eventsByType.viralLoadResultDatetime.0.data | date(format="%d/%m/%Y",timezone=timezone)}}
            {% endif %}
          </td>
          <td>
            {% if eventsByType.viralLoadResult.0 %}
              {{eventsByType.viralLoadResult.0.data}}
            {% endif %}
          </td>
          <td>
            {% if eventsByType.viralLoadTargetting.0 %}
              {{eventsByType.viralLoadTargetting.0.data}}
            {% endif %}
          </td>
          <td>{{patient.firstName}}</td>
          <td>{{patient.lastName}}</td>
          <td>
            {% if patient.gender == "FEMALE" %}
              Female
            {% elif patient.gender == "MALE" %}
              Male
            {% elif patient.gender == "TRANSGENDER" %}
              Transgender
            {% else %}
              {{patient.gender}}
            {% endif %}
          </td>
          <td>
            {% if enrolment.encounters.nodes.0 %}
              {{enrolment.encounters.nodes.0.startDatetime | date(format="%d/%m/%Y",timezone=timezone)}}
            {% endif %}
          </td>
          <td>{{patient.phone}}</td>
        </tr>
      {% endif %}
    {% endfor %}
    </tbody>
  </table>
  </div>
</body>
